$(document).ready(function(){
    $('.dissolution-class').live('keyup',function () {
        var sum = 0;
        var sum1=0;
        var answer=0;
        var answer1=0;
       var boxes= $('.dissolution-class[value!=""]').length;
        $('.dissolution-class').each(function() {
            sum += Number($(this).val());
           sum1=sum.toFixed(2);
           answer=sum1/boxes;
           answer1=answer.toFixed(2);
        });
        $('span#sa').text(sum1);
       $('span#sa1').text(answer1);
    });
});
 



